import {useState} from 'react';
import { Card, Button, Navbar, Container,  } from 'react-bootstrap';
import { Link } from "react-router-dom";
import Validation from './ValidationSignup';

function Signup() {
    const [values, setvalues] = useState({
        username:"",
        password:"",
        confirm:"",
        selection:"user"
    });

    const [errors, seterrors] = useState({});

    const submitForm = (event) => {
        event.preventDefault();
        let stat = Validation(values);
        seterrors(stat);
        if(Object.keys(stat).length === 0){
            //call an API function
        }
    };

    const changeHandler = (event) => {
        setvalues({
            ...values,
            [event.target.name]: event.target.value
        })
    };

    return (
        <>
        <Navbar className = "Navbar">
            <Container fluid>
                <Row>
                    <Col sm={3}><img src={logo} alt="Logo" className='logo'/></Col>
                </Row>
            </Container>
        </Navbar>
        <Card style={{ width: '18rem' }}>
            <form>
                <div>
                    <label>Username</label>
                    <input 
                    type="text" 
                    name="username" 
                    value={values.username}
                    onChange={changeHandler}
                    />
                    {errors.username && <p>{errors.username}</p>}
                </div>
                <div>
                    <label>password</label>
                    <input 
                    type="password" 
                    name="password"
                    value={values.password}
                    onChange={changeHandler}
                    />
                    {errors.password && <p>{errors.password}</p>}
                </div>
                <div>
                    <label>confirm password</label>
                    <input 
                    type="password" 
                    name="confirm"
                    value={values.confirm}
                    onChange={changeHandler}
                    />
                    {errors.confirm && <p>{errors.confirm}</p>}
                </div>
                <div>
                    <label>
                        <input
                        type="radio"
                        name="selection"
                        value="user"
                        checked={values.selection === "user"}
                        onChange={changeHandler}
                        />
                        User
                    </label>
                    <label>
                        <input
                        type="radio"
                        name="selection"
                        value="storage"
                        checked={values.selection === "storage"}
                        onChange={changeHandler}
                        />
                        Storage
                    </label>
                </div>
                <Button size="lg" onClick={submitForm}>sign up</Button>
                <p>if you have an account</p>
                <Link to="/signin" className="btn btn-primary btn-lg">sign in</Link>
            </form>
        </Card>
        </>
    );
}

export default Signup;
