import {useState} from 'react';
import Validation from './ValidationSignin';
import {Card, Button} from 'react-bootstrap'
import { BrowserRouter as Router, Link,  Switch, Route } from "react-router-dom";
import './Signin.css'

function Signin() {
    const [values, setvalues] = useState({
        username:"",
        password:"",
        selection:"user"
    });

    const [errors, seterrors] = useState({});

    const submitForm = (event) => {
        event.preventDefault();
        let stat = Validation(values);
        seterrors(stat);
        if(Object.keys(stat).length === 0){
            //call an API function
        }
    };

    const changeHandler = (event) => {
        setvalues({
            ...values,
            [event.target.name]: event.target.value
        })
    };

    return (
        <div className='cardParent'>
            <Card style={{ width: '18rem' }}>
                <form>
                    <div>
                        <label>Username</label>
                        <input 
                        type="text" 
                        name="username" 
                        value={values.username}
                        onChange={changeHandler}
                        />
                        {errors.username && <p>{errors.username}</p>}
                    </div>
                    <div>
                        <label>password</label>
                        <input 
                        type="password" 
                        name="password"
                        value={values.password}
                        onChange={changeHandler}
                        />
                        {errors.password && <p>{errors.password}</p>}
                    </div>
                    <div>
                        <label>
                            <input
                            type="radio"
                            name="selection"
                            value="user"
                            checked={values.selection === "user"}
                            onChange={changeHandler}
                            />
                            User
                        </label>
                        <label>
                            <input
                            type="radio"
                            name="selection"
                            value="storage"
                            checked={values.selection === "storage"}
                            onChange={changeHandler}
                            />
                            Storage
                        </label>
                    </div>
                    <Button size="lg" onSubmit={submitForm}>sign in</Button>
                    <p>if you don't have account sign up</p>
                    <Router>
                        <Link to='signup'> 
                            <Button size="lg">sign up</Button>
                        </Link>
                    </Router>
                    <Switch>
                        <Route path="/signin" component={Signin}/>
                        <Route path="/signup" component={Signup}/>
                        <Route path="/app" component={App}/>
                    </Switch>
                </form>
            </Card>
        </div>
    );
}

export default Signin;
