import {useState} from 'react';
import Validation from './ValidationSignin';
import { Card, Button, Navbar, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import logo from '../../decentorage.png';
import './Signin.css'

function Signin() {

    const [values, setvalues] = useState({
        username:"",
        password:"",
        walletAddress:"",
        availableSpace:'',
        selection:"user"
    });

    const [errors, seterrors] = useState({});

    const submitForm = (event) => {
        event.preventDefault();
        let stat = Validation(values);
        seterrors(stat);
        if(Object.keys(stat).length === 0){
            //call an API function
        }
    };

    const changeHandler = (event) => {
        setvalues({
            ...values,
            [event.target.name]: event.target.value
        })
    };

    return (
        <>
        <Navbar className = "Navbar">
            <Container fluid>
                <Row>
                    <Col sm={3}><img src={logo} alt="Logo" className='logo'/></Col>
                </Row>
            </Container>
        </Navbar>
        <div className='cardParent'>
            <Card style={{ width: '18rem' }}>
                <form>
                    <div>
                        <label>Username</label>
                        <input 
                        type="text" 
                        name="username" 
                        value={values.username}
                        onChange={changeHandler}
                        />
                        {errors.username && <p>{errors.username}</p>}
                    </div>
                    <div>
                        <label>password</label>
                        <input 
                        type="password" 
                        name="password"
                        value={values.password}
                        onChange={changeHandler}
                        />
                        {errors.password && <p>{errors.password}</p>}
                    </div>
                    <div>
                        <label>wallet address</label>
                        <input 
                        type="text" 
                        name="text"
                        value={values.walletAddress}
                        onChange={changeHandler}
                        />
                        {errors.password && <p>{errors.password}</p>}
                    </div>
                    <div>
                        <label>available space</label>
                        <input 
                        type="number" 
                        name="number"
                        value={values.password}
                        onChange={changeHandler}
                        />
                        {errors.password && <p>{errors.password}</p>}
                    </div>
                    <div>
                        <label>
                            <input
                            type="radio"
                            name="selection"
                            value="user"
                            checked={values.selection === "user"}
                            onChange={changeHandler}
                            />
                            User
                        </label>
                        <label>
                            <input
                            type="radio"
                            name="selection"
                            value="storage"
                            checked={values.selection === "storage"}
                            onChange={changeHandler}
                            />
                            Storage
                        </label>
                    </div>
                    <Button size="lg" onSubmit={submitForm}>sign in</Button>
                    <p>if you don't have an account sign up</p>
                    <Link to="signup" className="btn btn-primary btn-lg">sign up</Link>
                    <Link to="user" className="btn btn-primary btn-lg">user</Link>
                </form>
            </Card>
        </div>
        </>
    );
}

export default Signin;
