import { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { Navbar, Container, Row, Col, Button } from 'react-bootstrap'
import logo from '../../decentorage.png';
import axios from 'axios';
import web3 from '../contract/web3';
import './User.css';


function User() {
    useEffect(() => {
        getContracts()
        getPenddingContracts()
    }, []);

    const [contracts, setContracts] = useState([]);
    const [pendding, setPendding] = useState([]);
    const [values, setvalues] = useState({
        loggedout:false,
        nopendding:ture

    });

    const logOut = (event) => {
        console.log("here")
        event.preventDefault();
        values.loggedout = true;
        setvalues(values);
        console.log(values.loggedout);
    }

    const payforCreatingContract = () => {
        var decentorageAddress;
        axios.get('http://192.168.1.8:5000/user/getDecentorageWalletAddress',{
            headers: {
              'token': `${localStorage.getItem('accessToken')}`
            }
          }).then(async (response) => {
            console.log(response.status, response.data);
            decentorageAddress = response.data["decentorage_wallet_address"]
            const accounts = await web3.eth.getAccounts();
            // TODO: get the gas price and calculate the value of the contract deployment
            web3.eth.sendTransaction({to: decentorageAddress, from: accounts[0], value: 36000000});
        }).catch(error => {
            console.log('There was an error!', error);
        });
    }

    const getContracts = () => {
        axios.get('http://192.168.1.8:5000/user/getFiles',{
            headers: {
              'token': `${localStorage.getItem('accessToken')}`
            }
          }).then((response) => {
            console.log(response.status, response.data);
            const contracts = response;
            setContracts(contracts);
        }).catch(error => {
            console.log('There was an error!', error);
        });
    }

    const getPenddingContracts = () => {
        axios.get('http://192.168.1.8:5000/user/getContract',{
            headers: {
              'token': `${localStorage.getItem('accessToken')}`
            }
          }).then((response) => {
            console.log(response.status, response.data);
            const contracts = response;
            setPendding(contracts);
        }).catch(error => {
            console.log('There was an error!', error);
            const contracts = "no pendding contracts"
            setPendding(contracts);
        });
    }

    return (
        <>
        <Navbar className = "Navbar">
            <Container fluid>
                <Row>
                    <Col sm={3}><img src={logo} alt="Logo" className='logo'/></Col>
                    <Col sm={7}></Col>
                    <Col sm={2} className='logout-col'>
                        <Button size="lg" onClick={logOut} className="logout-button">log out</Button>
                        {values.loggedout && <Redirect to='/signin'/>}
                    </Col>
                </Row>
            </Container>
        </Navbar>
        <div className="pay-for-contract-button-container">
            <Button size="lg" className="pay-for-contract-button" onClick={payforCreatingContract}>pay for contract</Button>
        </div>
        <Row>
            <Col sm={6}>{ contracts !== null &&  <h1>{contractList}</h1>}</Col>
            <Col sm={6}>{ pending !== null &&  <h1>{penddingList}</h1>}</Col>
        </Row>
        </>
    );
}

export default User;
