import {useState} from 'react';
import { Card, Button, Navbar, Container, Row, Col } from 'react-bootstrap';
import { Link } from "react-router-dom";
import Validation from './ValidationSignup';
import logo from '../../decentorage.png';
import './Signup.css'

function Signup() {
    const [values, setvalues] = useState({
        username:"",
        password:"",
        confirm:"",
        walletAddress:"",
        availableSpace:'',
        selection:"user"
    });

    const [errors, seterrors] = useState({});

    const submitForm = (event) => {
        event.preventDefault();
        let stat = Validation(values);
        seterrors(stat);
        if(Object.keys(stat).length === 0){
            console.log(JSON.stringify({
                username: values.username,
                password: values.password,
                }))
            //call an API function
            if(values.selection === 'user'){
                // axios.post('http://192.168.1.8:5000/user/signup', {
                //     id: this.props.id
                //   })
                //     .then((response) => {
                //       //console.log(response.status);
                //     })
                fetch('http://192.168.1.8:5000/user/signup', {
                    method:'POST',
                    mode: 'no-cors',
                    header: {
                        'Content-Type': 'application/json'
                    },
                    body: {
                    "username": values.username,
                    password: values.password,
                    }
                }).then(response => {
                    console.log(response.text())
                })
            }

        }
    };

    const changeHandler = (event) => {
        setvalues({
            ...values,
            [event.target.name]: event.target.value
        })
    };

    return (
        <>
        <Navbar className = "Navbar">
            <Container fluid>
                <Row>
                    <Col sm={3}><img src={logo} alt="Logo" className='logo'/></Col>
                </Row>
            </Container>
        </Navbar>
        <Card style={{ width: '25rem' }} className="signup-card">
            <form>
                <div>
                    <label>Username</label>
                    <input 
                    type="text" 
                    name="username" 
                    value={values.username}
                    onChange={changeHandler}
                    className="input-style"
                    />
                    {errors.username && <p>{errors.username}</p>}
                </div>
                <div>
                    <label>password</label>
                    <input 
                    type="password" 
                    name="password"
                    value={values.password}
                    onChange={changeHandler}
                    className="input-style"
                    />
                    {errors.password && <p>{errors.password}</p>}
                </div>
                <div>
                    <label>confirm password</label>
                    <input 
                    type="password" 
                    name="confirm"
                    value={values.confirm}
                    onChange={changeHandler}
                    className="input-style"
                    />
                    {errors.confirm && <p>{errors.confirm}</p>}
                </div>
                <div>
                    <label>wallet address</label>
                    <input 
                    type="text" 
                    name="walletAddress"
                    value={values.walletAddress}
                    onChange={changeHandler}
                    disabled={values.selection === "user"}
                    className="input-style"
                    />
                    {errors.password && <p>{errors.walletAddress}</p>}
                </div>
                <div>
                    <label>available space</label>
                    <input 
                    type="number" 
                    name="availableSpace"
                    value={values.availableSpace}
                    onChange={changeHandler}
                    disabled={values.selection === "user"}
                    className="input-style"
                    />
                    {errors.password && <p>{errors.availableSpace}</p>}
                </div>
                {values.selection === "user" && <p>do not need to fill wallet address and available as user sign up</p>}
                <div>
                    <label>
                        <input
                        type="radio"
                        name="selection"
                        value="user"
                        checked={values.selection === "user"}
                        onChange={changeHandler}
                        />
                        User
                    </label>
                    <label>
                        <input
                        type="radio"
                        name="selection"
                        value="storage"
                        checked={values.selection === "storage"}
                        onChange={changeHandler}
                        />
                        Storage
                    </label>
                </div>
                <Button size="lg" onClick={submitForm} className="buttons-style">sign up</Button>
                <p>if you have an account</p>
                <Link to="/signin" className="btn btn-primary btn-lg buttons-style">sign in</Link>
            </form>
        </Card>
        </>
    );
}

export default Signup;
